/* Import futuristic fonts */
@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;700;900&family=Rajdhani:wght@300;400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

/* Luxurious Design System - Cinematic Grade Colors & Effects
All colors MUST be HSL for consistency and theming.
Premium Blue & Neon Blue Luxury Theme
*/

@layer base {
  :root {
    /* Luxurious Dark Foundation */
    --background: 220 25% 6%;
    --foreground: 210 20% 95%;

    /* Premium Card System */
    --card: 220 20% 8%;
    --card-foreground: 210 15% 92%;

    /* Elegant Popover */
    --popover: 220 25% 10%;
    --popover-foreground: 210 15% 95%;

    /* Luxury Blue Primary */
    --primary: 220 100% 65%;
    --primary-foreground: 220 25% 8%;
    --primary-glow: 200 100% 75%;

    /* Platinum Secondary */
    --secondary: 215 25% 15%;
    --secondary-foreground: 210 15% 90%;

    /* Sophisticated Muted */
    --muted: 215 20% 12%;
    --muted-foreground: 215 10% 65%;

    /* Premium Accent */
    --accent: 200 100% 70%;
    --accent-foreground: 220 25% 8%;

    /* Elegant Destructive */
    --destructive: 0 75% 60%;
    --destructive-foreground: 210 20% 95%;

    /* Refined Borders */
    --border: 215 15% 18%;
    --input: 215 15% 18%;
    --ring: 220 100% 65%;

    /* Premium Gradients */
    --gradient-luxury: linear-gradient(135deg, hsl(220 100% 65%), hsl(200 100% 75%));
    --gradient-platinum: linear-gradient(135deg, hsl(215 25% 15%), hsl(215 20% 20%));
    --gradient-royal: linear-gradient(135deg, hsl(200 100% 60%), hsl(180 100% 70%));
    --gradient-hero: linear-gradient(135deg, hsl(220 25% 6%), hsl(215 30% 12%));

    /* Cinematic Shadows */
    --shadow-luxury: 0 25px 50px -12px hsl(220 100% 65% / 0.4);
    --shadow-premium: 0 20px 40px -10px hsl(0 0% 0% / 0.6);
    --shadow-glow: 0 0 40px hsl(200 100% 75% / 0.3);
    --shadow-royal: 0 15px 35px -8px hsl(200 100% 60% / 0.3);

    /* Animation Durations */
    --transition-fast: 0.2s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-smooth: 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-luxury: 0.6s cubic-bezier(0.16, 1, 0.3, 1);

    --radius: 0.75rem;

    --sidebar-background: 0 0% 98%;

    --sidebar-foreground: 240 5.3% 26.1%;

    --sidebar-primary: 240 5.9% 10%;

    --sidebar-primary-foreground: 0 0% 98%;

    --sidebar-accent: 240 4.8% 95.9%;

    --sidebar-accent-foreground: 240 5.9% 10%;

    --sidebar-border: 220 13% 91%;

    --sidebar-ring: 217.2 91.2% 59.8%;
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;

    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;

    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;

    --primary: 210 40% 98%;
    --primary-foreground: 222.2 47.4% 11.2%;

    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;

    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;

    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;

    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;

    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }
}

/* Luxury Animation Classes */
@layer components {
  .luxury-glow {
    box-shadow: var(--shadow-glow);
    transition: all var(--transition-luxury);
  }

  .luxury-glow:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-luxury);
  }

  .premium-card {
    background: hsl(var(--card));
    border: 1px solid hsl(var(--border));
    box-shadow: var(--shadow-premium);
    transition: all var(--transition-smooth);
  }

  .premium-card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-luxury);
    border-color: hsl(var(--primary) / 0.3);
  }

  .gradient-text {
    background: var(--gradient-luxury);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .floating-animation {
    animation: float 6s ease-in-out infinite;
  }

  .fade-in-up {
    animation: fadeInUp 0.8s ease-out forwards;
    opacity: 0;
    transform: translateY(30px);
  }

  .stagger-1 { animation-delay: 0.1s; }
  .stagger-2 { animation-delay: 0.2s; }
  .stagger-3 { animation-delay: 0.3s; }
  .stagger-4 { animation-delay: 0.4s; }
  .stagger-5 { animation-delay: 0.5s; }
  .stagger-6 { animation-delay: 0.6s; }

  /* Button Gradient Styles */
  .bg-gradient-luxury {
    background: var(--gradient-luxury);
  }

  .bg-gradient-platinum {
    background: var(--gradient-platinum);
  }

  .bg-gradient-royal {
    background: var(--gradient-royal);
  }

  .shadow-glow {
    box-shadow: var(--shadow-glow);
  }

  .shadow-luxury {
    box-shadow: var(--shadow-luxury);
  }

  .shadow-premium {
    box-shadow: var(--shadow-premium);
  }

  .shadow-royal {
    box-shadow: var(--shadow-royal);
  }

  .duration-luxury {
    transition-duration: var(--transition-luxury);
  }

  .animate-fade-in {
    animation: fadeInUp 0.8s ease-out forwards;
  }

  /* Grid Background Pattern */
  .bg-grid {
    background-image: 
      linear-gradient(rgba(255, 255, 255, 0.1) 1px, transparent 1px),
      linear-gradient(90deg, rgba(255, 255, 255, 0.1) 1px, transparent 1px);
    background-size: 50px 50px;
  }

  .bg-grid-white\/\[0\.02\] {
    background-image: 
      linear-gradient(rgba(255, 255, 255, 0.02) 1px, transparent 1px),
      linear-gradient(90deg, rgba(255, 255, 255, 0.02) 1px, transparent 1px);
    background-size: 50px 50px;
  }

  /* Enhanced brain image effects */
  .brain-glow {
    filter: drop-shadow(0 0 30px rgba(59, 130, 246, 0.4)) 
            drop-shadow(0 0 60px rgba(34, 211, 238, 0.2)) 
            drop-shadow(0 0 90px rgba(99, 102, 241, 0.1));
  }

  .brain-container::before {
    content: '';
    position: absolute;
    inset: -20px;
    background: conic-gradient(from 0deg, transparent, rgba(59, 130, 246, 0.2), transparent, rgba(34, 211, 238, 0.2), transparent);
    border-radius: 50%;
    animation: rotate 20s linear infinite;
    z-index: -1;
  }

  @keyframes rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  /* Smooth scrolling */
  html {
    scroll-behavior: smooth;
  }

  /* Responsive container utilities */
  .container-responsive {
    width: 100%;
    margin-left: auto;
    margin-right: auto;
    padding-left: 1rem;
    padding-right: 1rem;
  }

  @media (min-width: 640px) {
    .container-responsive {
      max-width: 640px;
      padding-left: 1.5rem;
      padding-right: 1.5rem;
    }
  }

  @media (min-width: 768px) {
    .container-responsive {
      max-width: 768px;
      padding-left: 2rem;
      padding-right: 2rem;
    }
  }

  @media (min-width: 1024px) {
    .container-responsive {
      max-width: 1024px;
      padding-left: 2.5rem;
      padding-right: 2.5rem;
    }
  }

  @media (min-width: 1280px) {
    .container-responsive {
      max-width: 1280px;
      padding-left: 3rem;
      padding-right: 3rem;
    }
  }

  @media (min-width: 1536px) {
    .container-responsive {
      max-width: 1536px;
      padding-left: 4rem;
      padding-right: 4rem;
    }
  }

  /* Enhanced aspect ratio utilities */
  .aspect-square-responsive {
    aspect-ratio: 1 / 1;
    width: 100%;
    height: auto;
  }

  .aspect-video-responsive {
    aspect-ratio: 16 / 9;
    width: 100%;
    height: auto;
  }

  /* Responsive text scaling */
  .text-responsive-xs { font-size: clamp(0.75rem, 2vw, 0.875rem); }
  .text-responsive-sm { font-size: clamp(0.875rem, 2.5vw, 1rem); }
  .text-responsive-base { font-size: clamp(1rem, 3vw, 1.125rem); }
  .text-responsive-lg { font-size: clamp(1.125rem, 3.5vw, 1.25rem); }
  .text-responsive-xl { font-size: clamp(1.25rem, 4vw, 1.5rem); }
  .text-responsive-2xl { font-size: clamp(1.5rem, 5vw, 2rem); }
  .text-responsive-3xl { font-size: clamp(1.875rem, 6vw, 2.5rem); }
  .text-responsive-4xl { font-size: clamp(2.25rem, 7vw, 3rem); }
  .text-responsive-5xl { font-size: clamp(3rem, 8vw, 4rem); }
  .text-responsive-6xl { font-size: clamp(3.75rem, 10vw, 5rem); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes fadeInUp {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes pulse-ring {
  0% {
    transform: scale(1);
    opacity: 1;
  }
  80%, 100% {
    transform: scale(1.8);
    opacity: 0;
  }
}

@keyframes pulse-glow {
  0%, 100% {
    opacity: 0.4;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(1.1);
  }
}

@keyframes pulse-beam {
  0%, 100% {
    opacity: 0.3;
    transform: scaleY(1);
  }
  50% {
    opacity: 0.8;
    transform: scaleY(1.2);
  }
}

@keyframes spin-slow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes particle-float {
  0%, 100% {
    transform: translateY(0px) rotate(0deg);
    opacity: 0.6;
  }
  50% {
    transform: translateY(-20px) rotate(180deg);
    opacity: 1;
  }
}

@keyframes text-shimmer {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

/* Floating navigation hover scale effect */
.hover-scale {
  transition: all var(--transition-smooth);
}

.hover-scale:hover {
  transform: scale(1.05) translateY(-2px);
}

/* Neon glow effects for floating navigation */
.neon-glow {
  position: relative;
}

.neon-glow::before {
  content: '';
  position: absolute;
  inset: 0;
  border-radius: inherit;
  padding: 2px;
  background: linear-gradient(45deg, transparent, currentColor, transparent);
  mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
  mask-composite: exclude;
  -webkit-mask-composite: xor;
}

/* Luxury Floating Navigation Animations */
.animate-pulse-glow {
  animation: pulse-glow 2s ease-in-out infinite;
}

.animate-pulse-beam {
  animation: pulse-beam 3s ease-in-out infinite;
}

.animate-spin-slow {
  animation: spin-slow 4s linear infinite;
}

.animate-particle-float {
  animation: particle-float 2s ease-out infinite;
}

.luxury-text {
  background: linear-gradient(45deg, currentColor, transparent, currentColor);
  background-size: 200% 200%;
  background-clip: text;
  -webkit-background-clip: text;
  animation: text-shimmer 3s ease-in-out infinite;
}

.shadow-luxury-glow {
  box-shadow: 0 0 50px currentColor, 0 0 100px currentColor, inset 0 0 30px currentColor;
}

/* Service Navigation Floating Buttons */
.floating-nav-button {
  position: fixed;
  z-index: 1000;
  pointer-events: auto;
}

.floating-nav-button:hover {
  transform: scale(1.05);
}

@keyframes border-glow {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

@keyframes slide-in-left {
  from {
    transform: translateX(-100%) translateY(var(--y-offset, 0));
    opacity: 0;
  }
  to {
    transform: translateX(-75%) translateY(var(--y-offset, 0));
    opacity: 1;
  }
}

@keyframes slide-in-right {
  from {
    transform: translateX(100%) translateY(var(--y-offset, 0));
    opacity: 0;
  }
  to {
    transform: translateX(75%) translateY(var(--y-offset, 0));
    opacity: 1;
  }
}

@keyframes pulse-glow {
  0%, 100% {
    opacity: 0.4;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(1.05);
  }
}

@keyframes pulse-beam {
  0%, 100% {
    opacity: 0.3;
    height: 8rem;
  }
  50% {
    opacity: 0.8;
    height: 12rem;
  }
}

@keyframes spin-slow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes particle-float {
  0% {
    opacity: 0;
    transform: translateY(0) scale(0);
  }
  50% {
    opacity: 1;
    transform: translateY(-20px) scale(1);
  }
  100% {
    opacity: 0;
    transform: translateY(-40px) scale(0);
  }
}

@keyframes text-shimmer {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

